        margin: 0;
        padding: 0;
        cursor: pointer;
    }
    
    #ot_columnas .tab_selector li span {
        padding: 2px 6px;
        text-align: center;
        font-size: 14px;
        font-weight: bold;
        line-height: 24px;
    }
    
    #ot_columnas .ot-title {
        margin: 0 0 5px;
        line-height: 22px;
    }
    
    /* for prevent default joomla template css */
    #ot_columnas .tab_panel ul.menu {
        list-style-type: none;
        padding: 0;
    }
    #ot_columnas .tab_panel ul.menu a {
        background-position: 5px 50%;
    }
