<script type="text/javascript">
  $(document).ready(function(){
   $("#email-check").change(function(){ 
      var email = $("#email-check").val();
      var er    = new RegExp(/^[a-z0-9_\.\-]+@[a-z0-9_\.\-]*[a-z0-9_\-]+\.[a-z]{2,4}$/);
     if(email == ''){
      $("#email-check").addClass("red");
      $("#status").html('<font color="#cc0000">Ingrese um e-mail válido!</font>');        
     }else if(!er.exec(email)){
      $("#email-check").addClass("red");
      $("#status").html('<font color="#cc0000">Ingrese um e-mail válido!</font>');
     }else{
      $("#email-check").removeClass("red");
      $("#status").html('<img src="img/loader.gif" alt="" />&nbsp;Verificando...');
      $.ajax({  
        type: "POST",  
        url: "/email/",  
        data: "email="+ email,  
        success:function(msg){  
         if(msg == 1){
           $("#email-check").addClass("red");
           $("#status").html('<font color="#cc0000">E-mail já cadastrado!</font>');
         }else{
           $("#email-check").removeClass("red");
           $("#email-check").addClass("green");
           $("#status").html('<img src="img/ok.png" alt="" />');
         }
        }    
      }); 
     return false;
     }
   });
  });
</script>   